                                    token={this.makePayment}
                                    name={restaurant.name}
                                    amount={this.state.subTotal * 100}
                                    currency="INR"
                                    image={restaurant.thumb}
                                    shippingAddress
                                    billingAddress
                                    alipay
                                    bitcoin
                                >
                                    <button className="payment-form-button">Proceed</button>
                                   
                                </StripeCheckout>
